package com.abdurashidov.chattingimport com.abdurashidov.chatting.models.Userimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.database.FirebaseDatabaseobject MyData {    var phoneNumber: String? = null    val list = ArrayList<User>()    fun isOnline(online: String) {        val references = FirebaseDatabase.getInstance().getReference("users")        val map = HashMap<String, Any>()        map.put("online", online)        references.child(FirebaseAuth.getInstance().uid.toString()).updateChildren(map)    }}