package com.abdurashidov.chatting.adaptersimport android.graphics.Colorimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.chatting.Rimport com.abdurashidov.chatting.databinding.RvItemBindingimport com.abdurashidov.chatting.models.Userimport com.squareup.picasso.Picassoclass GroupAdapter(val list:ArrayList<String>, val rvClick: RvClick): RecyclerView.Adapter<GroupAdapter.Vh>() {    inner class Vh(val rvItem: RvItemBinding): RecyclerView.ViewHolder(rvItem.root){        fun onBind(user: String){            rvItem.name.text=user            rvItem.userName.visibility=View.GONE            rvItem.rvImg.setBackgroundColor(Color.parseColor("#095693"))            rvItem.rvImg.setImageResource(R.drawable.ic_single_group)            rvItem.main.setOnClickListener {                rvClick.onClick(user)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(RvItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }    interface RvClick{        fun onClick(label: String)    }}