package com.abdurashidov.chatting.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.chatting.databinding.ItemGroupFromBindingimport com.abdurashidov.chatting.databinding.ItemGroupToBindingimport com.abdurashidov.chatting.models.GroupMessageclass GroupMessageAdapter(val list: List<GroupMessage>, val uid:String): RecyclerView.Adapter<RecyclerView.ViewHolder>() {    inner class FromVh(val itemFromBinding: ItemGroupFromBinding):RecyclerView.ViewHolder(itemFromBinding.root){        fun onBind(message: GroupMessage){            itemFromBinding.messageTv.text=message.message            itemFromBinding.dateTv.text=message.date        }    }    inner class ToVh(val itemToBinding: ItemGroupToBinding):RecyclerView.ViewHolder(itemToBinding.root){        fun onBind(message: GroupMessage){            itemToBinding.messageTv.text=message.message            itemToBinding.dateTv.text=message.date        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerView.ViewHolder {        if (viewType==1){            return FromVh(ItemGroupFromBinding.inflate(LayoutInflater.from(parent.context), parent, false))        }else{            return ToVh(ItemGroupToBinding.inflate(LayoutInflater.from(parent.context), parent, false))        }    }    override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {        if (getItemViewType(position)==1){            val fromVh=holder as FromVh            fromVh.onBind(list[position])        }else{            val toVh=holder as ToVh            toVh.onBind(list[position])        }    }    override fun getItemViewType(position: Int): Int {        if (list[position].uid==uid){            return 1        }else{            return 2        }    }    override fun getItemCount(): Int = list.size}