package com.abdurashidov.chatting.adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.abdurashidov.chatting.ui.GroupFragmentimport com.abdurashidov.chatting.ui.UsersFragmentclass StateAdapters(val list:ArrayList<String>, fragment: Fragment)    : FragmentStateAdapter(fragment){    override fun getItemCount(): Int {        return list.size    }    override fun createFragment(position: Int): Fragment {        return when(position){            0->UsersFragment()            1->GroupFragment()            else->UsersFragment()        }    }}