package com.abdurashidov.chatting.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.chatting.databinding.RvItemBindingimport com.abdurashidov.chatting.models.Userimport com.squareup.picasso.Picassoclass UserAdapter(val list:ArrayList<User>, val rvClick: RvClick): RecyclerView.Adapter<UserAdapter.Vh>() {    inner class Vh(val rvItem: RvItemBinding): RecyclerView.ViewHolder(rvItem.root){        fun onBind(user: User){            rvItem.name.text=user.displayName            rvItem.userName.text=user.phoneNumber            Picasso.get().load(user.photoUrl).into(rvItem.rvImg)            rvItem.main.setOnClickListener {                rvClick.onClick(user)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(RvItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }    interface RvClick{        fun onClick(label: User)    }}